function prepareExam() {
  STATE.all = QUESTIONS.map(q => {
    // For each question, shuffle options but preserve correctIndex
    const clone = deepClone(q);
    const opts = clone.options.map((text, idx) => ({ text, originalIndex: idx }));
    shuffle(opts);
    const newOpts = opts.map(o => o.text);
    const correctText = clone.options[clone.correctIndex];
    const newCorrectIndex = newOpts.findIndex(t => t === correctText);
    clone.options = newOpts;
    clone.correctIndex = newCorrectIndex;
    return clone;
  });
  STATE.q = selectRandom(STATE.all, 60);
  STATE.answers = {};
  STATE.current = 0;
  STATE.remaining = STATE.durationSeconds;
  STATE.finished = false;
}

function mmss(totalSeconds) {
  const m = Math.floor(totalSeconds / 60);
  const s = totalSeconds % 60;
  return `${String(m).padStart(2,'0')}:${String(s).padStart(2,'0')}`;
}

function startTimer() {
  const tEl = document.getElementById('timer');
  clearInterval(STATE.timerId);
  STATE.timerId = setInterval(() => {
    if (STATE.finished) { clearInterval(STATE.timerId); return; }
    STATE.remaining--;
    if (STATE.remaining <= 0) {
      STATE.remaining = 0;
      clearInterval(STATE.timerId);
      submitExam();
    }
    tEl.textContent = mmss(STATE.remaining);
  }, 1000);
  tEl.textContent = mmss(STATE.remaining);
}

// Rendering
function renderPalette() {
  const pal = document.getElementById('palette');
  pal.innerHTML = '';
  STATE.q.forEach((q, idx) => {
    const b = document.createElement('button');
    b.textContent = idx + 1;
    b.classList.toggle('answered', STATE.answers[idx] !== undefined);
    b.classList.toggle('current', idx === STATE.current);
    b.addEventListener('click', () => { STATE.current = idx; renderQuestion(); });
    pal.appendChild(b);
  });
}

function renderQuestion() {
  const q = STATE.q[STATE.current];
  document.getElementById('qNumber').textContent = `Q${STATE.current + 1}`;
  document.getElementById('qType').textContent = q.type;
  document.getElementById('qBody').textContent = q.question;

  const form = document.getElementById('optionsForm');
  form.innerHTML = '';
  q.options.forEach((opt, idx) => {
    const id = `opt_${idx}`;
    const label = document.createElement('label');
    const input = document.createElement('input');
    input.type = 'radio';
    input.name = 'option';
    input.id = id;
    input.value = idx;
    input.checked = STATE.answers[STATE.current] === idx;
    input.addEventListener('change', () => {
      STATE.answers[STATE.current] = idx;
      renderPalette();
    });
    label.appendChild(input);
    label.appendChild(document.createTextNode(String.fromCharCode(65+idx) + '. ' + opt));
    form.appendChild(label);
  });

  renderPalette();
}

function updateButtons() {
  document.getElementById('submitBtn').disabled = STATE.q.length === 0;
  document.getElementById('restartBtn').disabled = STATE.q.length === 0;
}

function showSection(id) {
  ['intro','exam','result'].forEach(s => document.getElementById(s).classList.add('hidden'));
  document.getElementById(id).classList.remove('hidden');
}

function startExam() {
  prepareExam();
  showSection('exam');
  renderQuestion();
  updateButtons();
  startTimer();
}

function submitExam() {
  STATE.finished = true;
  // Evaluate
  let correct = 0;
  const details = [];
  const typeCounts = {};
  const typeCorrect = {};

  STATE.q.forEach((q, idx) => {
    typeCounts[q.type] = (typeCounts[q.type] || 0) + 1;
    const sel = STATE.answers[idx];
    const isCorrect = sel !== undefined && sel === q.correctIndex;
    if (isCorrect) {
      correct++;
      typeCorrect[q.type] = (typeCorrect[q.type] || 0) + 1;
    }
    details.push({
      number: idx + 1,
      type: q.type,
      question: q.question,
      selected: sel !== undefined ? q.options[sel] : null,
      correct: q.options[q.correctIndex],
      explanation: q.explanation,
      isCorrect
    });
  });

  const scoreEl = document.getElementById('score');
  const pct = Math.round((correct / STATE.q.length) * 100);
  scoreEl.textContent = `Score: ${correct}/${STATE.q.length} (${pct}%) — Time used: ${mmss(STATE.durationSeconds - STATE.remaining)}`;

  const breakdownEl = document.getElementById('breakdown');
  breakdownEl.innerHTML = '<strong>Breakdown by type</strong><br/>' +
    Object.keys(typeCounts).map(t => {
      const c = typeCounts[t];
      const k = typeCorrect[t] || 0;
      const p = Math.round((k/c)*100);
      return `${t}: ${k}/${c} (${p}%)`;
    }).join('<br/>');

  const reportEl = document.getElementById('report');
  reportEl.innerHTML = '';
  details.forEach(d => {
    const block = document.createElement('details');
    const sum = document.createElement('summary');
    sum.textContent = `Q${d.number} — ${d.isCorrect ? '✔ Correct' : '✖ Wrong'}`;
    block.appendChild(sum);
    const pre = document.createElement('pre');
    pre.textContent = d.question;
    block.appendChild(pre);
    const pSel = document.createElement('p');
    pSel.textContent = 'Your answer: ' + (d.selected ?? 'Not answered');
    block.appendChild(pSel);
    const pCorr = document.createElement('p');
    pCorr.textContent = 'Correct answer: ' + d.correct;
    block.appendChild(pCorr);
    const pExp = document.createElement('p');
    pExp.textContent = 'Explanation: ' + d.explanation;
    block.appendChild(pExp);
    reportEl.appendChild(block);
  });

  // Mark palette
  const pal = document.getElementById('palette');
  pal.querySelectorAll('button').forEach((b, i) => {
    b.classList.remove('current');
    const sel = STATE.answers[i];
    const isCorrect = sel !== undefined && sel === STATE.q[i].correctIndex;
    b.classList.add(isCorrect ? 'correct' : 'wrong');
  });

  document.getElementById('submitBtn').disabled = true;
  showSection('result');
}

function restartExam() {
  STATE.finished = false;
  STATE.answers = {};
  STATE.current = 0;
  STATE.remaining = STATE.durationSeconds;
  showSection('exam');
  renderQuestion();
  updateButtons();
  startTimer();
}

// Navigation
function initEvents() {
  document.getElementById('startBtn').addEventListener('click', startExam);
  document.getElementById('submitBtn').addEventListener('click', submitExam);
  document.getElementById('restartBtn').addEventListener('click', restartExam);
  document.getElementById('newExamBtn').addEventListener('click', startExam);
  document.getElementById('prevBtn').addEventListener('click', () => {
    STATE.current = Math.max(0, STATE.current - 1);
    renderQuestion();
  });
  document.getElementById('nextBtn').addEventListener('click', () => {
    STATE.current = Math.min(STATE.q.length - 1, STATE.current + 1);
    renderQuestion();
  });
}

// Bootstrap
window.addEventListener('DOMContentLoaded', () => {
  initEvents();
  document.getElementById('restartBtn').disabled = true;
});
